<div class="form-group row">    <div class="col-sm-12">        <input class="form-control" type="text" name="block-title" placeholder="Заголовок блока" value="">    </div></div><div class="form-group row">    <div class="col-sm-4 box">        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-width" value="100" id="img-width1" checked="checked"                data-width="1200" data-height="675">            <label class="form-check-label" for="img-width1">100%</label>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-width" value="50" id="img-width2"                   data-width="600" data-height="337">            <label class="form-check-label" for="img-width2">50%</label>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-width" value="30" id="img-width3"                   data-width="300" data-height="169">            <label class="form-check-label" for="img-width3">30%</label>        </div>    </div>    <div class="col-sm-4 box percent-100">        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-horizontally" value="left" id="img-horizontally1" checked="checked">            <label class="form-check-label" for="img-horizontally1">слева</label>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-horizontally" value="centre" id="img-horizontally2">            <label class="form-check-label" for="img-horizontally2">в центре</label>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-horizontally" value="right" id="img-horizontally3">            <label class="form-check-label" for="img-horizontally3">справа</label>        </div>    </div>    <div class="col-sm-4 box">        <div class="form-group percent-100">            <div class="form-check form-switch">                <input type="hidden" name="flow" value="0">                <input class="form-control form-check-input" type="checkbox" name="flow" value="1">                <label class="form-check-label">Обтекание текстом</label>            </div>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-vertically" value="before" id="img-vertically1">            <label class="form-check-label" for="img-vertically1">сверху</label>        </div>        <div class="form-group percent-100 form-check">            <input class="form-check-input" type="radio" name="img-vertically" value="centre" id="img-vertically2">            <label class="form-check-label" for="img-vertically2">в центре</label>        </div>        <div class="form-group form-check">            <input class="form-check-input" type="radio" name="img-vertically" value="after" id="img-vertically3">            <label class="form-check-label" for="img-vertically3">снизу</label>        </div>    </div></div><div class="form-group media-preview">    <label>        <input type="file" id="zzz" class="form-control fileupload block-image-upload">        <div class="head-image">            <img src="" alt="">        </div>    </label></div><div class="form-group row">    <div class="col-sm-12">        <input class="form-control" type="text" name="img-title" placeholder="Подпись картинки" value="">    </div></div><div class="form-group row">    <div class="col-sm-12">        <input class="form-control" type="text" name="img-link" placeholder="Ссылка" value="">    </div></div><div class="form-group">    <label class="form-control-label justify-content-center">Текст блока</label>    <textarea name="text" class="summernote form-control item-content"></textarea></div>